import React, { useEffect, useState } from "react"
import Modal from "react-modal"
import Swal from 'sweetalert2/dist/sweetalert2.js'
import 'sweetalert2/src/sweetalert2.scss'

function EditProduct(props) {
	const { onReload, item } = props

	const [showModal, setShowModal] = useState(false)
	const [data, setData] = useState(item)

	const [dataCategory, setDataCategory] = useState([]);
	useEffect(() => {
		const fetchApi = async () => {
			fetch('http://localhost:3005/category')
				.then((res) => res.json())
				.then(dataCategory => setDataCategory(dataCategory))
		};

		fetchApi()
	}, []);

	function openModal() {
		setShowModal(true);
	}
	function closeModal() {
		setShowModal(false);
	}

	const handleChange = (e) => {
		const name = e.target.name
		const value = e.target.value
		setData({
			...data,
			[name]: value
		})
	}

	console.log("data: " + data)

	const handleSubmit = (e) => {
		e.preventDefault();
		fetch("http://localhost:3005/products", {
			method: "POST",
			headers: {
				Accept: "application/json",
				"Content-Type": "application/json"
			},
			body: JSON.stringify(data)
		})
		setShowModal(false)
		onReload();
		Swal.fire({
			position: "top-end",
			icon: "success",
			title: "Tạo sản phẩm thành công",
			showConfirmButton: false,
			timer: 1500
		});
	}

	const customStyles = {
		content: {
			top: '50%',
			left: '50%',
			right: 'auto',
			bottom: 'auto',
			marginRight: '-50%',
			transform: 'translate(-50%, -50%)',
		},
	};

	return <><div><button onClick={openModal}> Chỉnh sửa</button>
		<Modal
			isOpen={showModal}
			onRequestClose={closeModal}
			style={customStyles}
			contentLabel="Example Modal"
		>
			<form action="" onSubmit={handleSubmit}>
				<table>
					<tbody>
						<tr>
							<td>Tiêu đề</td>
							<td>
								<input onChange={handleChange} type="text" name="title" value={data.title} />
							</td>
						</tr>
						<tr>
							<td>Danh mục</td>
							<td >
								<select name="category" onChange={handleChange}>
									{dataCategory.map((item, index) => ((
										<option key={index} value={data.ca} >{item}</option>

									)))}
								</select>
							</td>
						</tr>
						<tr>
							<td>Giá</td>
							<td>
								<input onChange={handleChange} type="text" name="price" />
							</td>
						</tr>
						<tr>
							<td>Giảm giá</td>
							<td>
								<input onChange={handleChange} type="text" name="discountPercentage" />
							</td>
						</tr>
						<tr>
							<td>Số lượng còn lại</td>
							<td>
								<input onChange={handleChange} type="text" name="stock" />
							</td>
						</tr>
						<tr>
							<td>Đường dẫn ảnh</td>
							<td>
								<input onChange={handleChange} type="text" name="thumbnail" />
							</td>
						</tr>
						<tr>
							<td>Mô tả</td>
							<td>
								<input onChange={handleChange} type="text" name="availabilityStatus" />
							</td>
						</tr>
						<tr>
							<td>
								<button onClick={closeModal}>Hủy</button>
							</td>
							<td>
								<button type="submit">Tạo mới</button>
							</td>
						</tr>
					</tbody>
				</table>
			</form>
		</Modal></div></>
}

export default EditProduct
